export async function generatePattern({ projectId }: { projectId: string }) {
  const hash = await hashUUID(projectId);
  const numericValues = convertHashToNumericValues(hash);

  const backgroundColor =
    colorCombinations[
      coerceToRange(
        Number(String(numericValues[0]).slice(0, 2)),
        0,
        colorCombinations.length,
      )
    ]?.backgroundColor ?? "hsla(258.5,59.4%,59.4%,1)";
  const foregroundColor =
    colorCombinations[
      coerceToRange(
        Number(String(numericValues[1]).slice(0, 2)),
        0,
        colorCombinations.length,
      )
    ]?.foregroundColor ?? "hsla(340, 82%, 52%, 1)";

  const randomPatternIndex =
    SVGs[
      coerceToRange(
        Number(String(numericValues[2]).slice(0, 2)),
        0,
        SVGs.length,
      )
    ];

  console.log("RandomPatternIndex", randomPatternIndex);
  console.log(
    "INDEX",
    coerceToRange(Number(String(numericValues[2]).slice(0, 2)), 0, SVGs.length),
  );

  if (!randomPatternIndex?.image) return;
  let backgroundImage = randomPatternIndex.image;

  const randomStrokeWidth = coerceToRange(
    Number(String(numericValues[3]).slice(0, 2)),
    1,
    3,
  );
  const randomRotation = coerceToRange(
    Number(String(numericValues[4]).slice(0, 1)),
    0,
    99,
  );
  const randomScale = coerceToRange(
    Number(String(numericValues[4]).slice(0, 1)),
    1,
    3,
  );

  backgroundImage = findAndReplaceInWord(backgroundImage, [
    {
      name: "stroke-width",
      newValue: randomStrokeWidth.toString(),
    },
    {
      name: "stroke",
      newValue: [foregroundColor, backgroundColor],
    },
    {
      name: "patternTransform",
      newValue: `scale(${randomScale}) rotate(${randomRotation})`,
    },
  ]);

  console.log(backgroundColor, backgroundImage);

  return {
    backgroundColor: backgroundColor,
    backgroundImage: backgroundImage,
  };
}

function findAndReplaceInWord(
  svgString: string,
  attributes: {
    name: string;
    newValue: string | string[];
  }[],
) {
  const svgDataRegex = /url\("data:image\/svg\+xml,(.*?)"\)/;
  const svgDataMatch = svgDataRegex.exec(svgString);
  if (!svgDataMatch || svgDataMatch.length < 2 || !svgDataMatch[1]) {
    throw new Error("Invalid SVG background image string");
  }

  // Decode the SVG data from URL-encoded format to a regular string
  let svgData = svgDataMatch[1];

  // Loop through all provided attributes
  attributes.forEach(({ name, newValue }) => {
    // If newValue is an array, we handle multiple values for the same attribute
    if (Array.isArray(newValue)) {
      let count = 0; // Counter to keep track of how many times we've replaced the attribute

      // Replace each occurrence of the attribute with the corresponding value from the array
      svgData = svgData.replace(new RegExp(`${name}='[^']*'`, "g"), (match) => {
        const value = newValue[count];
        count = Math.min(count + 1, newValue.length - 1); // Increment but ensure it doesn't go out of bounds
        return `${name}='${value}'`;
      });
    } else {
      // If it's a single value, replace all occurrences of the attribute with the same value
      const attrRegex = new RegExp(`${name}='[^']*'`, "g");

      if (svgData.match(attrRegex)) {
        svgData = svgData.replace(attrRegex, `${name}='${newValue}'`);
      } else {
        // If the attribute does not exist, add it to the <svg> or main element
        svgData = svgData.replace(/<svg[^>]*>/, (match) => {
          return `${match} ${name}='${newValue}'`;
        });
      }
    }
  });

  // Return the updated background image string
  return `url("data:image/svg+xml,${svgData}")`;
}

// Function to convert the hash into five numeric values
function convertHashToNumericValues(hash: string) {
  const numericValues = [];

  // Split the hash into 5 parts, each part is 12 hex characters (48 bits)
  for (let i = 0; i < 5; i++) {
    const part = hash.slice(i * 12, (i + 1) * 12); // 12 characters for each numeric value
    const numericValue = parseInt(part, 16); // Convert hex to integer
    numericValues.push(numericValue);
  }

  return numericValues;
}

async function hashUUID(uuid: string) {
  // Encode the UUID as a byte array
  const encoder = new TextEncoder();
  const data = encoder.encode(uuid);

  // Hash the UUID with SHA-256
  const hashBuffer = await crypto.subtle.digest("SHA-256", data);

  // Convert the hash buffer to a hex string
  const hashArray = Array.from(new Uint8Array(hashBuffer));
  const hashHex = hashArray
    .map((byte) => byte.toString(16).padStart(2, "0"))
    .join("");

  return hashHex;
}

function coerceToRange(num: number, min: number, max: number) {
  // Calculate the modulus with the range size
  const rangeSize = max - min + 1;
  const mod = (((num - min) % rangeSize) + rangeSize) % rangeSize; // Ensure positive result

  // Return the number within the range
  return mod + min;
}
